let textIndex = 0;
let charIndex = 0;
let isDeleting = false;
let typingSpeed = 100; // ุณุฑุนุช ุชุงูพ (ฺฉูุชุฑ = ุณุฑุนุชุฑ)
let deleteSpeed = 50;  // ุณุฑุนุช ูพุงฺฉ ฺฉุฑุฏู (ฺฉูุชุฑ = ุณุฑุนุชุฑ)
let pauseBetween = 1500; // ูฺฉุซ ุจู ูุชูโูุง
function typeEffect() {
    const currentText = texts[textIndex];
    
    if (isDeleting) {
        // ุญุงูุช ูพุงฺฉโฺฉุฑุฏู
        typingText.innerHTML = currentText.substring(0, charIndex - 1) + '<span class="cursor"></span>';
        charIndex--;
        
        if (charIndex === 0) {
            isDeleting = false;
            textIndex = (textIndex + 1) % texts.length;
            setTimeout(typeEffect, typingSpeed);
        } else {
            setTimeout(typeEffect, deleteSpeed);
        }
    } else {
        // ุญุงูุช ุชุงูพ ฺฉุฑุฏู
        typingText.innerHTML = currentText.substring(0, charIndex + 1) + '<span class="cursor"></span>';
        charIndex++;
        
        if (charIndex === currentText.length) {
            isDeleting = true;
            setTimeout(typeEffect, pauseBetween);
        } else {
            setTimeout(typeEffect, typingSpeed);
        }
    }
}
