function animateCounters() {
    const counterElements = document.querySelectorAll('.header-details__item h2');
    const speed = 3000; // ูุฏุช ุฒูุงู ฺฉู ุงููุดู (ููโุซุงูู)
    let started = false;
    if (started) return;
    started = true;
    // ุชุงุจุน ุชุจุฏู ุงุนุฏุงุฏ ูุงุฑุณ ุจู ุงูฺฏูุณ
    const toEnglishNum = (num) => {
        return num.replace(/[^0-9ฐ-น]/g, '').replace(/[ฐ-น]/g, d => 'ฐฑฒณดตถทธน'.indexOf(d));
    };
    // ูุญุงุณุจู ุฒูุงู ุจุฑุง ูุฑ ุดูุงุฑูุฏู ุจุฑ ุงุณุงุณ ูุณุจุช ููุฏุงุฑ ููุง
    const targets = Array.from(counterElements).map(counter => {
        const originalText = counter.innerText;
        const target = +toEnglishNum(originalText);
        return { counter, target, originalText };
    }).filter(item => !isNaN(item.target));
    if (targets.length === 0) return;
    const maxTarget = Math.max(...targets.map(item => item.target));
    const startTime = performance.now();
    const updateCounters = (currentTime) => {
        const elapsed = currentTime - startTime;
        const progress = Math.min(elapsed / speed, 1); // ูพุดุฑูุช ุงุฒ 0 ุชุง 1
        targets.forEach(({ counter, target }) => {
            const currentValue = Math.floor(progress * target);
            counter.innerText = new Intl.NumberFormat('fa-IR').format(currentValue);
        });
        if (progress < 1) {
            requestAnimationFrame(updateCounters);
        } else {
            // ุงุทููุงู ุงุฒ ุฑุณุฏู ุจู ููุงุฏุฑ ุฏูู ููุง
            targets.forEach(({ counter, target }) => {
                counter.innerText = new Intl.NumberFormat('fa-IR').format(target);
            });
        }
    };
    requestAnimationFrame(updateCounters);
}
// ุงุฌุงุฏ Intersection Observer ุจุฑุง ุชุดุฎุต ููุงุด ุงููุงูโูุง
function setupCounterObserver() {
    const observerOptions = {
        root: null, // ูุดุงูุฏู ูุณุจุช ุจู viewport
        rootMargin: '0px', // ุญุงุดู ุงุถุงู ูุฏุงุฑุฏ
        threshold: 0.5 // ููุช 50% ุงููุงู ุฏุฏู ุดุฏ
    };
    const observer = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                animateCounters();
                observer.unobserve(entry.target); // ุฏฺฏุฑ ูุงุฒ ุจู ูุดุงูุฏู ูุณุช
            }
        });
    }, observerOptions);
    // ูุดุงูุฏู ุชูุงู ุงููุงูโูุง ุดูุงุฑูุฏู
    document.querySelectorAll('.header-details__item h2').forEach(counter => {
        observer.observe(counter);
    });
}
